import { useState, useEffect } from "react";
import { createStage } from "../gameHelpers";

export const useStage = (player, resetPlayer) => {
    const [stage, setStage] = useState(createStage());
    const [rowsCleared, setRowsCleared] = useState(0);

    useEffect(() => {
        setRowsCleared(0);

        const sweepRows = (newStage) => 
            newStage.reduce((ack, row) => {
                if (row.findIndex((cell) => cell[0] === 0) === -1) {
                    setRowsCleared((prev) => prev + 1);
                    ack.unshift(new Array(newStage[0].length).fill([0, 'clear']));
                    return ack;
                }
                ack.push(row);
                return ack;
            }, []);

        const updateStage = (prevStage) => {
            // First flush the stage
            const newStage = prevStage.map((row) =>
                row.map((cell) => (cell[1] === "clear" ? [0, "clear"] : cell))
            );

            // Then draw the tetromino
            player.tetromino.forEach((row, y) => {
                row.forEach((value, x) => {
                    if (value !== 0) {
                        const newY = y + (player.pos.y || 0);
                        const newX = x + (player.pos.x || 0);

                        // Ensure the cell is within the boundaries of the stage
                        if (newStage[newY] && newStage[newY][newX] !== undefined) {
                            newStage[newY][newX] = [
                                value,
                                `${player.collided ? "merged" : "clear"}`,
                            ];
                        }
                    }
                });
            });

            // then check if we collided
            if (player.collided) {
                resetPlayer();
                // Don't update rowsCleared here; it will be handled in the next frame
                return sweepRows(newStage);
            }
            return newStage;
        };

        setStage((prev) => updateStage(prev));
    }, [player, resetPlayer]);

    return [stage, setStage, rowsCleared];
};
